#!/usr/bin/env bash

function hook_chunks() {
	log "hooking chunks"
	echo "ffff081f00 01 09 0a 0e 0f 15 21 00 31 00 41 42 44 45 47 49 4d 9401 8402 8602 8702 8902 00 9602 00 00 00 00 00 f60a8004" > $TEMP/world/0000000000000000

	map=""
	for (( i=0; i<4097; i++ )); do
		map+="$(printf '%02x' $((RANDOM%15)))"
	done

	echo "$map" >> $TEMP/world/0000000000000000

	echo "" >> $TEMP/world/0000000000000000

	pkt_chunk FFFFFFFF FFFFFFFF 
	pkt_chunk FFFFFFFF 00000000
	pkt_chunk FFFFFFFF 00000001

	pkt_chunk 00000000 FFFFFFFF
	pkt_chunk 00000000 00000000
	pkt_chunk 00000000 00000001
	
	pkt_chunk 00000001 FFFFFFFF
	pkt_chunk 00000001 00000000
	pkt_chunk 00000001 00000001
}

